$(document).ready((function(){$("#loginForm").submit((function(e){e.preventDefault();const s=$("#email").val(),a=$("#password").val();$.ajax({url:"/auth/login",type:"POST",data:JSON.stringify({email:s,password:a}),contentType:"application/json",success:function(e){$("#message").html('<div class="alert alert-success">Login successful!</div>'),setTimeout((function(){location.href="./dashboard.html"}),200)},error:function(e){$("#message").html('<div class="alert alert-danger"><strong>Login failed:</strong> '+JSON.parse(e.responseText).message+'<br/>You may <a href="./register.html">register here</a>.</div>')}})}))}));